<?php	/*	*Posts Release By Timing Admin  Options view	*/	include_once dirname( __FILE__ ).'/TimingRelease.class.php';	include_once dirname( __FILE__ ).'/PRBT-actions.php';	$args=array(		'orderby' => 'ID',		'order' => 'ASC'		);	$categories=get_categories($args);		//get the current date as the default time	$html_now_time = current_time('mysql');	list( $today_year, $today_month, $today_day, $today_hour, $today_minute, $today_second ) = split( '([^0-9])', $html_now_time);?><div class="wrap">	<h2><?php _e('Posts Release By Timing Options','posts-release-by-timing');?></h2>	<h4><?php _e('Attention:before you start option it,must be sure that you have been save will release posts as "draft" posts and install "WP Missed Schedule Fix Failed Future Posts" Plugin(which decide the timing posts release time and amounts),if it not be installed,start to install it now! Otherwise your timing posts will publish failed!' , 'posts-release-by-timing');?></h4>	<table border="1" style="text-align:center;border:1px solid green;border-spacing:0;">		<tr style ="background:#FFFBCC;">			<td><?php _e('Category Name', 'posts-release-by-timing');?></td>			<td><?php _e('Category ID', 'posts-release-by-timing');?></td>			<td><?php _e('Future Posts', 'posts-release-by-timing');?></td>			<td><?php _e('Draft Posts', 'posts-release-by-timing');?></td>			<td><?php _e('Last post timing date', 'posts-release-by-timing');?></td>		</tr>		<?php foreach($categories as $category){?>		<tr>			<td style="text-align:left;"><?php 					echo $category->name.'</td><td>'.$category->cat_ID.'</td>'; 					$obj = new TimingRelease($category->cat_ID); $future_posts_count = $obj->prbt_category_future_count();					$draft_posts_count = $obj->prbt_category_draft_count();					$future_lastpost_date = $obj->prbt_category_lastpost_time();					echo '<td>'.$future_posts_count.'</td><td>'.$draft_posts_count.'</td><td>'.$future_lastpost_date;					?>			</td>		</tr>			<?php }?>	</table><br><br>		<form method="post" action="<?php echo $_SERVER['PHP_SELF']; ?>?page=<?php echo plugin_basename(__FILE__);?>">		<h5><?php _e('Start Timing Posts:', 'posts-release-by-timing');?></h5>		<?php _e('Category ID:', 'posts-release-by-timing');?>		<input type="text" name="catID">(<?php _e('only One ID once', 'posts-release-by-timing');?>)<br>		<?php _e('Start time:', 'posts-release-by-timing');?>		<input type="text" name="year" size="4" maxlength="4" value="<?php echo $today_year;?>"><?php _e('year', 'posts-release-by-timing');?>		<select name="month">			<?php 				for($i=1;$i<=12;$i++){					if($i==$today_month){			?>				<option value="<?php printf("%02d",$i);?>" selected><?php printf("%02d",$i);?></option>			<?php	}else{ ?>				<option value="<?php printf("%02d",$i);?>"><?php printf("%02d",$i);?></option>			<?php }	}?>			</select><?php _e('month', 'posts-release-by-timing');?>		<input type="text" name="day" size="2" maxlength="2" value="<?php echo $today_day;?>"><?php _e('day', 'posts-release-by-timing');?>		<input type="text" name="hour" size="2" maxlength="2" value="<?php echo $today_hour;?>"> : 		<input type="text" name="minute" size="2" maxlength="2" value="<?php echo $today_minute;?>"><br>		<?php _e('Every time interval:', 'posts-release-by-timing');?>		<input type="text" name="perTime" size="2" maxlength="2"><?php _e('hours', 'posts-release-by-timing');?><br>		<?php _e('Every time release quantity:', 'posts-release-by-timing');?>		<input type="text" name="amounts"><?php _e('posts', 'posts-release-by-timing');?><br>		<?php _e('Execution times:', 'posts-release-by-timing');?>		<input type="text" name="numbers"><?php _e('times(frequency)', 'posts-release-by-timing');?><br>		<p>		<input type="submit" value="<?php _e('submit', 'posts-release-by-timing');?>">		</p>		<h5><?php _e('Reset posts status "draft" in which category ID were timing:', 'posts-release-by-timing');?></h5>		<?php _e('Reset category ID:', 'posts-release-by-timing');?>		<input type="text" name="draft">		<input type="submit" value="<?php _e('submit', 'posts-release-by-timing');?>">	</form></div>